/* patID:1058  A+B in Hogwarts*//* Running for PAT! *//* starting time:2016-7-19 1:09 PM *//* spare 20 min Accepted! */#include<iostream>using namespace std;typedef struct Check{	int kunt;	int sickle;	long int gallon;}Check;int main(){	Check a,b;	long int kunts_a;	long int kunts_b;	long int kunts_total;	scanf("%ld.%d.%d",&a.gallon,&a.sickle,&a.kunt);	scanf("%ld.%d.%d",&b.gallon,&b.sickle,&b.kunt);	kunts_a=17*29*a.gallon+29*a.sickle+a.kunt;	kunts_b=17*29*b.gallon+29*b.sickle+b.kunt;	kunts_total=kunts_a+kunts_b;	int a1,a2,a3;	a1=kunts_total/(17*29);	a2=(kunts_total%(17*29))/29;	a3=(kunts_total%(17*29))%29;	printf("%d.%d.%d\n",a1,a2,a3);	return 0;}