/* 1069. The Black Hole of Numbers (20) *//* starting at 2016-9-3 12:28 pm *//* 18/20 13:07 *//* 20/20 13:16 */#include<iostream>#include<vector>#include<sstream>#include<string>#include<algorithm>#include<cstring>using namespace std;bool cmp(const char &a,const char &b){	return a>b;}int main(){	string num;	int max,min;	cin>>num;    while(num.size()<4) num.append("0");	stringstream ss;	sort(num.begin(),num.end(),cmp);	ss<<num;	ss>>max;	sort(num.begin(),num.end());	ss.clear();	ss<<num;	ss>>min;	printf("%04d - %04d = %04d\n",max,min,max-min);	while(max-min!=6174&&max-min!=0)	{		string num1;		ss.clear();		ss<<(max-min);		ss>>num1;		while(num1.size()<4) num1.append("0");		ss.clear();		sort(num1.begin(),num1.end(),cmp);	    ss<<num1;	    ss>>max;	    sort(num1.begin(),num1.end());	    ss.clear();	    ss<<num1;	    ss>>min;	    printf("%04d - %04d = %04d\n",max,min,max-min);	}	//if(max-min!=6174&&max-min!=0)printf("%04d - %04d = %04d\n",max,min,max-min);	return 0;		}