/* 1077. Kuchiguse (20) *//* starting  at 2016-9-3 13:34 *//* accepted! at 2016-9-3 14:00 *//* fuck!!!!!! why freopen() can add a space in the last???*/#include<iostream>#include<vector>#include<sstream>#include<string>#include<algorithm>#include<cstring>using namespace std;string common(string &str1,string &str2){    //cout<<"str1---"<<str1<<endl;    //cout<<"str2---"<<str2<<endl;	int i=str1.size()-1;	int j=str2.size()-1;	string res="";	for(;i>=0&&j>=0;i--,j--)	{		//cout<<"+++str1"<<i<<" :"<<str1[i]<<endl;		//cout<<"+++str2"<<j<<" :"<<str2[j]<<endl;		if(str1[i]==str2[j])		{			res.append(1,str1[i]);		}		else break;	}	reverse(res.begin(),res.end());	return res;}int main(){	freopen("D:\\in.txt","r",stdin);	//ifstream cin("D:\\in.txt");	int n;	scanf("%d\n",&n);	string str1,str2;	getline(cin,str1);	n--;	while(n--)	{		getline(cin,str2); 		str1=common(str1,str2);		if(str1==""){			str1="nai";			break;		}	}	cout<<str1<<endl;	return 0;		}