/* PAT 1092. To Buy or Not to Buy (20)*//* starting at 2016-8-15 19:31 *//* 19:46 20/20 */#include<iostream>#include<vector>#include<string>#include<set>#include<map>using namespace std;int main(){	freopen("D:\\in.txt","r",stdin);	string str1;	string str2;	map<char,int> map1; 	cin>>str1>>str2;	bool flag=true;   //mark	map<char,int>::iterator iter;	for(int i=0;i<str1.size();i++){		iter=map1.find(str1[i]);		if(iter==map1.end()) map1.insert(make_pair(str1[i],1));		else ++map1[str1[i]];	}	int cnt=0;	for(int i=0;i<str2.size();i++){		iter=map1.find(str2[i]);		if(iter==map1.end()||map1[str2[i]]<=0){			flag=false;			cnt++;		}		else{			--map1[str2[i]];		}	}	if(flag) cout<<"Yes "<<str1.size()-str2.size()<<endl;	else cout<<"No "<<cnt<<endl;    return 0;}
