/* PAT 1112 Stucked keyboard *//* 18/20 */#include<cstring>#include<cstdio>#include<algorithm>#include<iostream>#include<set>#include<map>using namespace std;int main(){	freopen("D:\\in.txt","r",stdin);	char s[1005];	int k;	scanf("%d\n",&k);	scanf("%s",s);	set<char> normalSet;	char cur_c=s[0];	int same_len=0;	int len=strlen(s);	s[len+1]='#';	s[len+2]='\0';	int i;	for(i=0;s[i]!='\0';i++)	{		if(cur_c==s[i]) same_len++;		else if(cur_c!=s[i]){			if(same_len%k!=0){				normalSet.insert(s[i-1]);			}			cur_c=s[i];			same_len=1;		}	}	//cout<<"we insert:"<<cur_c<<endl;	set<char>::iterator iter,iter2;	set<char> brokenSet;	//cout<<"*****set size"<<normalSet.size()<<endl;	for(int i=0;s[i]!='\0';i++){		iter=normalSet.find(s[i]);		if(iter==normalSet.end()){			iter2=brokenSet.find(s[i]);			if(iter2==brokenSet.end()) printf("%c",s[i]);			brokenSet.insert(s[i]);		}				}	printf("\n");	for(int i=0;s[i]!='\0';){		iter=normalSet.find(s[i]);		if(iter!=normalSet.end()){			printf("%c",s[i]);			i++;		}		else{			printf("%c",s[i]);			i+=k;		}	}	return 0;}