 string name;	  map<string,vector<int> > student_class;	  //vector<string> courses[k+1];      for(int i=0;i<K;i++){		  cin>>id;		  cin>>numStu;		  while(numStu--){			  cin>>name;			  student_class[name].push_back(id);		  }	  }      while(N--){		  string temp;		  cin>>temp;		  cout<<temp;		  vector<int>::iterator iter=student_class[temp].begin();		  sort(iter,student_class[temp].end());		  int num=student_class[temp].size();		  if(num!=0){			  cout<<" "<<num;			  for(;iter!=student_class[temp].end();iter++){				  cout<<" "<<*iter;			  }		  }else cout<<" "<<0; 		  cout<<endl;	  }	  return 0;  }